const { app, BrowserWindow, ipcMain } = require('electron');
const path = require('path');
const { createRequire } = require('module');
// Lazy-load electron-log to avoid startup crash if not present/packed
let log = null;
// Lazy Sentry load to prevent crash if package not found in packaged app
let Sentry = null;
let init = null;
let IPCMode = null;
const { setupIpcHandlers } = require('./ipc-handlers');
// Try to load build-time Sentry config (generated in CI) for packaged apps
let sentryConfig = null;
try {
  // This file is generated by scripts/generate-sentry-config.js during CI/build
  // and bundled into the Electron app via electron-builder files config
  sentryConfig = require('./sentry-config');
  console.log('üîç [SENTRY] Loaded build-time sentry-config.js');
} catch (_) {
  console.log('üîç [SENTRY] No build-time sentry-config.js found');
}
require('dotenv').config();

// Try resolve/load Sentry early for diagnostics (before any init)
try {
  // Resolve modules relative to the app root (inside app.asar when packaged)
  const appRoot = (() => {
    try {
      return app.getAppPath();
    } catch (_) {
      return __dirname;
    }
  })();
  const appRequire = createRequire(path.join(appRoot, '/'));
  const resolvedPath = appRequire.resolve('@sentry/electron/main');
  console.log('üîç [SENTRY] Resolved @sentry/electron/main at:', resolvedPath);
  const sentryMain = appRequire('@sentry/electron/main');
  Sentry = sentryMain;
  init = sentryMain.init;
  IPCMode = sentryMain.IPCMode;
} catch (err) {
  console.error('‚ùå [SENTRY] Cannot resolve/load @sentry/electron/main:', err && err.message ? err.message : err);
}

// Initialize Sentry as early as possible
// Use env var first; in packaged builds, fall back to generated config; in dev use hardcoded DSN
const devDsn = 'https://c525bad84d7baf7a00631c940b44a980@o4506526838620160.ingest.us.sentry.io/4510187648712704';
const sentryDsn = process.env.NATIVE_SENTRY_DSN_MAIN
  || (app.isPackaged ? (sentryConfig && sentryConfig.mainDsn) : devDsn);
if (sentryDsn && typeof init === 'function' && IPCMode) {
  init({
    dsn: sentryDsn,
    
    // Debug mode - logs to console
    debug: true,
    
    // Use protocol-based IPC for robust cross-process context merging
    ipcMode: IPCMode.Protocol,
    
    // Environment detection
    environment: app.isPackaged ? 'production' : 'development',
    
    // Release version
    release: 'flock-native@0.4.8',
    
    // Sample rate for performance monitoring
    tracesSampleRate: app.isPackaged ? 0.1 : 1.0,
    
    // Filter sensitive data
    beforeSend(event, hint) {
      // Remove sensitive environment variables
      if (event.contexts?.runtime?.env) {
        const env = event.contexts.runtime.env;
        delete env.BLUESKY_PASSWORD;
        delete env.BLUESKY_USERNAME;
        delete env.NATIVE_SENTRY_DSN;
        delete env.SENTRY_DSN; // Also filter generic one just in case
      }
      
      // Remove sensitive breadcrumbs
      if (event.breadcrumbs) {
        event.breadcrumbs = event.breadcrumbs.filter(breadcrumb => {
          const msg = breadcrumb.message || '';
          if (msg.includes('password') || 
              msg.includes('token') || 
              msg.includes('BLUESKY_PASSWORD')) {
            return false;
          }
          return true;
        });
      }
      
      return event;
    },
    
    // Ignore common noise
    ignoreErrors: [],
  });
  
  console.log('‚úÖ [SENTRY] Initialized for Electron main process');
  console.log('üîç [SENTRY] Environment:', app.isPackaged ? 'production' : 'development');
  console.log('üîç [SENTRY] Using:', process.env.NATIVE_SENTRY_DSN_MAIN || (sentryConfig && sentryConfig.mainDsn) ? 'production DSN' : 'development DSN');
} else {
  if (!sentryDsn) {
    console.log('üîç [SENTRY] No NATIVE_SENTRY_DSN_MAIN found, error tracking disabled');
    console.log('üîç [SENTRY] Set NATIVE_SENTRY_DSN_MAIN environment variable to enable error tracking in production');
  }
  if (!init) {
    console.log('üîç [SENTRY] init function unavailable (module not loaded). Skipping Sentry init.');
  }
}

// Keep a global reference to prevent garbage collection
let mainWindow;

function createWindow() {
  // Create the browser window
  mainWindow = new BrowserWindow({
    width: 1200,
    height: 900,
    minWidth: 800,
    minHeight: 600,
    // show: false, // Temporarily commented out for debugging
    webPreferences: {
      preload: path.join(__dirname, 'preload.js'),
      nodeIntegration: false,
      contextIsolation: true,
      sandbox: false // Required for file system access
    },
    title: 'Flock Native - Bluesky Social Migrator',
    icon: path.join(__dirname, '../public/icon.png')
  });

  // Setup IPC handlers (pass Sentry instance if available)
  setupIpcHandlers(mainWindow, Sentry);

  // Load the app
  // Use app.isPackaged to reliably detect production vs development
  // Allow override with ELECTRON_IS_PACKAGED env var for testing
  const isDev = !app.isPackaged && !process.env.ELECTRON_IS_PACKAGED;
  
  if (isDev) {
    // Development mode - load from dev server
    const startUrl = process.env.ELECTRON_START_URL || 'http://localhost:4201';
    mainWindow.loadURL(startUrl);
    
    // Open DevTools in development
    mainWindow.webContents.openDevTools();
  } else {
    // Production mode - load from built files
    const appPath = app.getAppPath();
    let indexPath;
    
    if (app.isPackaged) {
      // In packaged app, files are in dist/flock-native/browser/
      indexPath = path.join(appPath, 'dist/flock-native/browser/index.html');
    } else {
      // In local test (ELECTRON_IS_PACKAGED=1), need to go to project root
      // appPath is projects/flock-native/electron, need to go up 3 levels
      const projectRoot = path.join(appPath, '../../..');
      indexPath = path.join(projectRoot, 'dist/flock-native/browser/index.html');
    }
    
    console.log('üîç [PROD] Loading from:', indexPath);
    console.log('üîç [PROD] App path:', appPath);
    console.log('üîç [PROD] Is packaged:', app.isPackaged);
    console.log('üîç [PROD] process.execPath:', process.execPath);
    
    mainWindow.loadFile(indexPath).catch(err => {
      console.error('‚ùå Failed to load index.html:', err);
      console.error('üìÇ App path:', appPath);
      console.error('üìÑ Index path:', indexPath);
    });
  }

  // Handle load failures
  mainWindow.webContents.on('did-fail-load', (event, errorCode, errorDescription, validatedURL) => {
    console.error('‚ùå Page failed to load:', errorDescription, 'URL:', validatedURL);
  });

  // Show window when ready to prevent flash of unstyled content
  let windowShown = false;
  
  mainWindow.once('ready-to-show', () => {
    if (!windowShown) {
      console.log('‚úÖ Window ready to show');
      mainWindow.show();
      windowShown = true;
    }
  });
  
  // Fallback: show window after 3 seconds if ready-to-show doesn't fire
  setTimeout(() => {
    if (!windowShown && mainWindow && !mainWindow.isDestroyed()) {
      console.log('‚ö†Ô∏è Fallback: showing window after timeout');
      mainWindow.show();
      windowShown = true;
    }
  }, 3000);

  // Log when page loads
  mainWindow.webContents.on('did-finish-load', () => {
    console.log('‚úÖ Page loaded successfully');
  });

  // Handle window closed
  mainWindow.on('closed', () => {
    mainWindow = null;
  });

  // Handle external links
  mainWindow.webContents.setWindowOpenHandler(({ url }) => {
    require('electron').shell.openExternal(url);
    return { action: 'deny' };
  });
}

// App lifecycle

// This method will be called when Electron has finished initialization
app.whenReady().then(() => {
  // Configure electron-log (lazy)
  try {
    const appRoot = app.getAppPath();
    const appRequire = createRequire(path.join(appRoot, '/'));
    // Prefer explicit main entry; fallback to package root
    try {
      const resolvedMain = appRequire.resolve('electron-log/main');
      console.log('üîç [LOG] Resolved electron-log (main) at:', resolvedMain);
      log = appRequire('electron-log/main');
    } catch (_) {
      const resolvedRoot = appRequire.resolve('electron-log');
      console.log('üîç [LOG] Resolved electron-log at:', resolvedRoot);
      log = appRequire('electron-log');
    }
    if (log && typeof log.initialize === 'function') {
      log.initialize();
    }
    const level = process.env.ELECTRON_LOG_LEVEL || 'silly';
    if (log && log.transports && log.transports.file) {
      // Force a stable path under userData so our script can tail it
      try {
        const logsDir = path.join(app.getPath('userData'), 'logs');
        if (require('fs').existsSync(logsDir) === false) {
          require('fs').mkdirSync(logsDir, { recursive: true });
        }
        log.transports.file.resolvePath = () => path.join(logsDir, 'main.log');
      } catch (_) {}
      log.transports.file.level = level;
    }
    if (log && log.transports && log.transports.console) {
      log.transports.console.level = level;
    }
    if (log && typeof log.catchErrors === 'function') {
      log.catchErrors({ showDialog: false });
    }
    // Mirror console methods to electron-log
    const originalLog = console.log;
    const originalWarn = console.warn;
    const originalError = console.error;
    console.log = (...a) => { try { log && log.log && log.log(...a); } catch (_) {} originalLog(...a); };
    console.warn = (...a) => { try { log && log.warn && log.warn(...a); } catch (_) {} originalWarn(...a); };
    console.error = (...a) => { try { log && log.error && log.error(...a); } catch (_) {} originalError(...a); };
    try {
      const filePath = log && log.transports && log.transports.file && log.transports.file.getFile ? log.transports.file.getFile().path : 'unknown';
      console.log('üîç [LOG] electron-log initialized. File:', filePath);
    } catch (_) {
      console.log('üîç [LOG] electron-log initialized.');
    }
  } catch (e) {
    console.error('‚ùå [LOG] Failed to load electron-log (non-fatal):', e && e.message ? e.message : e);
  }

  createWindow();

  app.on('activate', () => {
    // On macOS re-create window when dock icon is clicked
    if (BrowserWindow.getAllWindows().length === 0) {
      createWindow();
    }
  });
});

// Quit when all windows are closed, except on macOS
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

// Handle app termination
app.on('before-quit', () => {
  console.log('ü¶Ö Eagle preparing to land...');
});

// Log when ready
app.on('ready', () => {
  console.log('ü¶Ö Flock Native - Eagle is ready to soar!');
  console.log(`üìÇ App Path: ${app.getAppPath()}`);
  console.log(`üè† User Data: ${app.getPath('userData')}`);
});

// Error handling
process.on('uncaughtException', (error) => {
  console.error('‚ùå Uncaught Exception:', error);
  
  // Send to Sentry
  if (sentryDsn && Sentry && typeof Sentry.captureException === 'function') {
    Sentry.captureException(error, {
      level: 'fatal',
      tags: {
        process: 'main',
        type: 'uncaughtException'
      }
    });
  }
});

process.on('unhandledRejection', (error) => {
  console.error('‚ùå Unhandled Rejection:', error);
  
  // Send to Sentry
  if (sentryDsn && Sentry && typeof Sentry.captureException === 'function') {
    Sentry.captureException(error, {
      level: 'error',
      tags: {
        process: 'main',
        type: 'unhandledRejection'
      }
    });
  }
});

